css:
/* Mozambique Print Format Base Styles */

.print-format {
    font-family: 'Arial', 'Helvetica', sans-serif;
    font-size: 12px;
    line-height: 1.5;
    color: #333;
    background: #fff;
}

/* Document Header */
.document-header {
    border-bottom: 1px solid #e5e5e5;
    padding-bottom: 8px;
    margin-bottom: 12px;
}

.document-title {
    color: #2c3e50;
    font-size: 24px;
    font-weight: 600;
    margin: 0;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.document-subtitle {
    color: #7f8c8d;
    font-size: 12px;
    margin: 2px 0 0 0;
    font-weight: 400;
}

.document-number {
    font-size: 16px;
    color: #2c3e50;
    margin-bottom: 2px;
}

.document-date {
    font-size: 12px;
    color: #7f8c8d;
}

/* Document Status */
.document-status-cancelled {
    background-color: #fdf2f2;
    border: 1px solid #fecaca;
    padding: 8px;
    margin: 10px 0;
    border-radius: 4px;
    text-align: center;
}

.document-status-cancelled h3 {
    color: #dc2626;
    margin: 0;
    font-size: 16px;
    font-weight: 600;
}

/* Section Titles */
.section-title {
    color: #2c3e50;
    font-size: 14px;
    font-weight: 600;
    margin: 0 0 6px 0;
    padding-bottom: 4px;
    border-bottom: 1px solid #e5e5e5;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

/* Customer and Invoice Details */
.customer-invoice-section {
    margin-bottom: 12px;
}

.customer-details, .invoice-details {
    padding: 0;
}

.customer-name {
    color: #2c3e50;
    font-size: 14px;
    font-weight: 600;
    margin-bottom: 4px;
    display: block;
}

.customer-info {
    font-size: 12px;
    line-height: 1.4;
    color: #555;
}

.invoice-info {
    font-size: 12px;
}

.info-row {
    display: flex;
    justify-content: space-between;
    margin-bottom: 4px;
    padding: 2px 0;
}

.info-row .label {
    color: #7f8c8d;
    font-weight: 500;
}

.info-row .value {
    color: #2c3e50;
    font-weight: 500;
}

/* Items Table */
.items-section {
    margin-bottom: 12px;
}

.items-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 6px;
    border: 1px solid #e5e5e5;
}

.items-table th {
    background-color: #f8f9fa;
    color: #2c3e50;
    padding: 8px 6px;
    font-weight: 600;
    font-size: 11px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    border-bottom: 1px solid #e5e5e5;
    text-align: left;
}

.items-table th.text-center {
    text-align: center;
}

.items-table th.text-right {
    text-align: right;
}

.items-table td {
    padding: 6px 6px;
    border-bottom: 1px solid #f0f0f0;
    vertical-align: top;
    font-size: 12px;
}

.items-table tbody tr:last-child td {
    border-bottom: none;
}

.items-table tbody tr:nth-child(even) {
    background-color: #fafafa;
}

.item-description {
    font-size: 12px;
    line-height: 1.4;
}

/* Totals Section */
.totals-section {
    margin-bottom: 12px;
}

.amount-in-words, .terms-section {
    padding: 8px 0;
    margin-bottom: 8px;
}

.amount-in-words h5, .terms-section h5 {
    color: #2c3e50;
    margin: 0 0 6px 0;
    font-size: 12px;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.amount-in-words p, .terms-section p {
    margin: 0;
    font-size: 12px;
    line-height: 1.4;
    color: #555;
}

.totals-table {
    padding: 0;
}

.totals-row {
    display: flex;
    justify-content: space-between;
    padding: 3px 0;
    border-bottom: 1px solid #f0f0f0;
}

.totals-row:last-child {
    border-bottom: none;
}

.totals-label {
    color: #555;
    font-size: 12px;
    font-weight: 500;
}

.totals-value {
    color: #2c3e50;
    font-size: 12px;
    font-weight: 500;
}

.totals-row.grand-total {
    border-top: 2px solid #2c3e50;
    margin-top: 6px;
    padding-top: 8px;
    font-weight: 600;
    font-size: 14px;
}

.totals-row.grand-total .totals-label,
.totals-row.grand-total .totals-value {
    font-weight: 600;
    font-size: 14px;
}

/* Payment Section */
.payment-section {
    margin-bottom: 12px;
}

.payment-info {
    background-color: #fef3c7;
    border: 1px solid #f59e0b;
    padding: 8px;
    border-radius: 4px;
}

.payment-info h5 {
    color: #92400e;
    margin: 0 0 6px 0;
    font-size: 12px;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.payment-info p {
    margin: 2px 0;
    font-size: 12px;
    color: #92400e;
}

/* QR Code Section */
.qr-section {
    margin-bottom: 8px;
}

.qr-code-container {
    text-align: center;
    padding: 6px 0;
}

.qr-code-img {
    max-width: 100px;
    max-height: 100px;
    width: auto;
    height: auto;
    border: 1px solid #e5e5e5;
    border-radius: 4px;
}

.qr-label {
    color: #7f8c8d;
    font-size: 10px;
    margin: 6px 0 0 0;
    font-style: italic;
}

/* Utility Classes */
.text-center { text-align: center; }
.text-left   { text-align: left; }
.text-right  { text-align: right; }
.text-uppercase { text-transform: uppercase; }

.page-break { page-break-inside: avoid; }

/* Print Specific Styles */
@media print {
    .print-format { font-size: 11px; }
    .document-title { font-size: 20px; }
    .items-table th, .items-table td { padding: 6px 4px; }
    .totals-row { padding: 2px 0; }
    .payment-info { padding: 6px; }
}
